import {
  Button,
  Flex,
  Image,
  Text,
  Link,
  Box,
  useDisclosure,
  ModalOverlay,
  ModalContent,
  ModalCloseButton,
  ModalBody,
} from "@chakra-ui/react";
import { MdAccountBalanceWallet } from "react-icons/md";
import { IoIosAdd } from "react-icons/io";
import NextLink from "next/link";
import { Modal } from "@chakra-ui/react";
import { walletContext } from "../../utils/walletContext";
import WalletConnectProvider from "@walletconnect/web3-provider";
import { useState, useContext } from "react";
import Web3 from "web3";
declare const window: any;
export default function NavigationBar() {
  const [address, setAddress] = useState<string>("");
  const [balance, setBalance] = useState<string>("");
  const [wallet, setWallet] = useContext(walletContext);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const enviroment: any = process.env.NEXT_PUBLIC_ENV;
  const chainid: any = process.env.NEXT_PUBLIC_CHAIN_ID;
  // console.log(chainid);
  const web3 = new Web3(process.env.NEXT_PUBLIC_END_POINT as string);
  const provider = new WalletConnectProvider({
    rpc: {
      [chainid]: process.env.NEXT_PUBLIC_ENDPOINT as string,
    },
  });
  const polygon = {
    chainId: 137,
    chainIdHex: "0x89",
    name: "Polygon (Matic)",
    shortName: "Polygon",
    img: "https://raw.githubusercontent.com/sushiswap/icons/master/network/polygon.jpg",
    enabled: true,
    addData: {
      chainId: "0x89",
      chainName: "Polygon Mainnet",
      nativeCurrency: {
        name: "MATIC",
        symbol: "MATIC",
        decimals: 18,
      },
      rpcUrls: ["https://polygon-rpc.com"],
      blockExplorerUrls: ["https://polygonscan.com/"],
    },
  };

  const mdcontent = [
    {
      title: "Metamask",
      description: "Connect to Metamask",
      icon: "data:image/svg+xml;base64,",
    },
    {
      title: "Wallet Connect",
      description: "Connect to your wallet",
      icon: "data:image/svg+xml;base64,",
    },
  ];
  // console.log(provider);

  async function loadAccounts() {
    let windowType = window;

    let accounts = await windowType.ethereum.request({
      method: "eth_requestAccounts",
    });
    console.log(windowType.ethereum.chainId);

    if (windowType.ethereum.chainId == process.env.NEXT_PUBLIC_CHAIN_ID) {
      setAddress(accounts[0]);
      let bal = await web3.eth.getBalance(accounts[0]);
      let ethBal: any = await web3.utils.fromWei(bal, "ether");
      setBalance(ethBal);

      setWallet({
        balance: ethBal,
        address: accounts[0],
      });
    } else {
      try {
        await windowType.ethereum.request({
          method: "wallet_switchEthereumChain",
          params: [
            {
              chainId: web3.utils.toHex(
                process.env.NEXT_PUBLIC_CHAIN_ID as string
              ),
            },
          ],
        });
      } catch (switchError: any) {
        // This error code indicates that the chain has not been added to MetaMask.
        if (switchError.code === 4902) {
          try {
            await windowType.ethereum.request({
              method: "wallet_addEthereumChain",
              params: [polygon.addData],
            });
          } catch (addError) {
            console.log("Error Adding chain: ", addError);
          }
        } else {
          console.log("Error Switching Chains: ", switchError);
        }
      }
    }
  }
  const handleWalletConnect = async () => {
    await provider.enable();

    //  Create Web3 instance
    const web3 = new Web3(provider as any);
    window.w3 = web3;
    let accounts = await web3.eth.getAccounts();
    // console.log(window.w3.chainId, process.env.NEXT_PUBLIC_CHAIN_ID);
    const chainId = await (await web3.eth.getChainId()).toString();
    if (chainId == process.env.NEXT_PUBLIC_CHAIN_ID) {
      setAddress(accounts[0]);
      let bal = await web3.eth.getBalance(accounts[0]);
      let ethBal: any = await web3.utils.fromWei(bal, "ether");
      setBalance(ethBal);

      setWallet({
        balance: ethBal,
        address: accounts[0],
      });
    }
  };

  const disconnect = async () => {
    // Close provider session
    await provider.disconnect();
    provider.on("disconnect", (code: number, reason: string) => {
      console.log(code, reason);
    });
  };

  return (
    <Flex
      justify="space-between"
      px="8"
      position="relative"
      zIndex={2}
      maxW="1600px"
      mx="auto"
      py="6"
      alignItems="center"
      color="white"
    >
      <NextLink href="/" passHref>
        <Link _hover={{}} _focus={{}} _active={{}}>
          <Image src="assets/logo.svg" alt="Metapass" w="10" />
        </Link>
      </NextLink>
      <Flex alignItems="center" experimental_spaceX="6">
        <NextLink href="/create" passHref>
          <Link _hover={{}} _focus={{}} _active={{}}>
            <Button
              pl="1"
              rounded="full"
              bg="whiteAlpha.800"
              color="blackAlpha.700"
              fontWeight="medium"
              _hover={{ shadow: "sm", bg: "white" }}
              border="2px"
              borderColor="white"
              _focus={{}}
              _active={{ transform: "scale(0.95)" }}
              role="group"
              leftIcon={
                <Flex
                  _groupHover={{ transform: "scale(1.05)" }}
                  transitionDuration="200ms"
                  justify="center"
                  alignItems="center"
                  color="white"
                  bg="brand.gradient"
                  rounded="full"
                  p="0.5"
                >
                  <IoIosAdd />
                </Flex>
              }
            >
              Create Event
            </Button>
          </Link>
        </NextLink>
        {balance ? (
          <Button
            rounded="full"
            color="white"
            bg="blackAlpha.500"
            border="2px"
            _hover={{ bg: "blackAlpha.600" }}
            _focus={{}}
            _active={{ bg: "blackAlpha.700" }}
            py="5"
            fontWeight="normal"
            leftIcon={<MdAccountBalanceWallet />}
            onClick={disconnect}
          >
            {balance.substring(0, 5)} MATIC
          </Button>
        ) : (
          <>
            <Button
              rounded="full"
              color="white"
              bg="blackAlpha.500"
              border="2px"
              _hover={{ bg: "blackAlpha.600" }}
              _focus={{}}
              _active={{ bg: "blackAlpha.700" }}
              py="5"
              fontWeight="normal"
              leftIcon={<MdAccountBalanceWallet />}
              onClick={onOpen}
            >
              Connect Wallet
            </Button>
            <Modal isOpen={isOpen} onClose={onClose}>
              <ModalOverlay />
              <ModalContent>
                <ModalCloseButton />
                <ModalBody m={2} p={4}>
                  {mdcontent.map((item: any, index: number) => {
                    return (
                      <Flex
                        key={index}
                        flexDirection="column"
                        alignItems="center"
                        borderRadius="md"
                        as="button"
                        _hover={{ bg: "gray.100" }}
                        onClick={
                          index == 1 ? handleWalletConnect : loadAccounts
                        }
                      >
                        <Flex
                          justify="space-between"
                          alignItems="center"
                          px="4"
                          py="4"
                        >
                          <Text fontSize="lg" fontWeight="medium">
                            {item.title}
                          </Text>
                        </Flex>
                        <Image src={item.icon} alt="icon" w="10%" />
                        <Flex
                          justify="space-between"
                          alignItems="center"
                          px="4"
                          py="4"
                        >
                          <Text
                            fontSize="md"
                            fontWeight="normal"
                            color="gray.400"
                          >
                            {item.description}
                          </Text>
                        </Flex>
                      </Flex>
                    );
                  })}
                </ModalBody>
              </ModalContent>
            </Modal>
          </>
        )}
      </Flex>
    </Flex>
  );
}
